import Foundation

var A = [Int]()
var B = [Int]()
var result = 0

func check(_ i: Int, _ j: Int) {
  var tempResult = 0, ptrA = i, ptrB = j
  while ptrA < A.count && ptrB < B.count && A[ptrA] == B[ptrB] {
    tempResult += 1
    ptrA += 1
    ptrB += 1
  }
  if tempResult > result { result = tempResult }
}

func findLength(_ arrayA: [Int], _ arrayB: [Int]) -> Int {
  A = arrayA
  B = arrayB
  if A.count == 0 || B.count == 0 { return 0 }
  for i in 0..<A.count {
    if A.count - i < result { break }
    for j in 0..<B.count {
      if B.count - j < result { break }
      check(i, j)
    }
  }
  return result
}

