import Foundation

var A = [Int]()
var B = [Int]()
var memos = [[Int]]()

func check(_ i: Int, _ j: Int) -> Int {
  if i < 0 || j < 0 { return 0 }
  if A[i] == B[j] {
    if memos[i][j] == -1 {
      let temp = 1 + check(i - 1, j - 1)
      memos[i][j] = temp
      return temp
    } else {
      return memos[i][j]
    }
  } else {
    memos[i][j] = 0
    return 0
  }
}

func findLength(_ arrayA: [Int], _ arrayB: [Int]) -> Int {
  A = arrayA
  B = arrayB
  var result = 0
  memos = Array(repeating: Array(repeating: -1, count: B.count), count: A.count)
  if A.count == 0 || B.count == 0 { return 0 }
  for i in 0..<A.count {
    for j in 0..<B.count {
      let temp = check(i, j)
      if temp > result { result = temp }
    }
  }
  return result
}

